mapper.insert:

insert into interview (id, user_id, written_examination, 
  first_interview, second_interview, 
  first_assessment, second_assessment
  )
values (#{id,jdbcType=INTEGER}, #{userId,jdbcType=INTEGER}, #{writtenExamination,jdbcType=LONGVARCHAR}, 
  #{firstInterview,jdbcType=LONGVARCHAR}, #{secondInterview,jdbcType=LONGVARCHAR}, 
  #{firstAssessment,jdbcType=LONGVARCHAR}, #{secondAssessment,jdbcType=LONGVARCHAR}
  )

mapper.select:
select id, user_id, written_examination, first_interview, second_interview, first_assessment,
second_assessment
from interview where user_id = #{id}
